  function pageFunction(totalItems) {
    //window.alert(totalItems)
    var x ="<ul class=\"pagination\">";     
      for (i = 1; i <= totalItems; i++) {
        x=x+"<li><a href=\"#\">"+i+"</a></li>";
      }

    x=x+"</ul>";
    //window.alert(x)
    document.getElementById("paging").innerHTML = x;
}


